$(document).ready(function(){var o=0,t=$('input[name="todoInput"]');$("button").click(function(){o+=1,$("h3").html("You have "+o+" todos!");var n=$(t).val(),i=$("<li>"+n+"</li>");$("ul").append(i).hide().slideDown(),$(t).val("")}),$(document).on("click","ul li",function(){$(this).remove(),o-=1,$("h3").html("You have "+o+" todos!")}),$("h4").click(function(){o=0,$("h3").html("Your todo list is empty!"),$("ul").empty()})});